import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: Scaffold(
        body: Padding(
          padding: const EdgeInsets.all(15.0),
          child: Column(
            children: [
              //Logo
              Image.network(
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTohsHpg8w1H61Amh3MSQGQJx1RNR0XjQFu4g&s",
              ),
              SizedBox(height: 10),
              //Bike
              Image.network(
                "data:image/jpeg;base64,",
              ),
              //Bike Name
              Text(
                "Classic 350",
                style: TextStyle(
                  fontSize: 30,
                  fontWeight: FontWeight.bold,
                  fontStyle: FontStyle.italic,
                ),
              ),
              SizedBox(height: 10),
              //Specification
              Text(
                "Specification",
                style: TextStyle(
                  fontSize: 30,
                  fontWeight: FontWeight.bold,
                  color: Colors.red,
                ),
              ),
              SizedBox(height: 10),
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  buildContainer(
                    "data:image/jpeg;base64,",
                    "350 CC",
                  ),
                  buildContainer(
                    "https://png.pngtree.com/png-vector/20220627/ourmid/pngtree-element-silhouette-technical-engine-gear-shape-png-image_5437691.png",
                    "40 PS",
                  ),
                  buildContainer(
                    "https://static.vecteezy.com/system/resources/previews/018/923/766/non_2x/mileage-auto-part-icon-png.png",
                    "30.7 KM",
                  ),
                  buildContainer(
                    "https://png.pngtree.com/png-vector/20220519/ourmid/pngtree-bicycle-repair-disk-brake-icon-png-image_4666254.png",
                    "Disk",
                  ),
                ],
              ),
              SizedBox(height: 10),
              Container(
                height: 150,
                width: double.infinity,
                decoration: BoxDecoration(
                  image: DecorationImage(
                    image: NetworkImage(
                      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRl3x9JNTYO_iW9YpDILB--Yhw-P8xf_92A7Q&s",
